INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1160','Soziales','''Soziales'' bezeichnet eine Fläche auf der vorwiegend Gebäude des Sozialwesens stehen, z. B. Kindergärten, Jugend- und Senioreneinrichtungen, Freizeit-, Fremden- und Obdachlosenheime.','funktion','ax_flaechebesondererfunktionalerpraegung');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','NAM','''Name'' ist der Eigenname von ''Fläche besonderer funktionaler Prägung'' insbesondere außerhalb von Ortslagen.','ax_flaechebesondererfunktionalerpraegung','DLKM','name');
