INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_flugverkehr','DLKM','42015','REO','[E] ''Flugverkehr'' umfasst die baulich geprägte Fläche und die mit ihr in Zusammenhang stehende Freifläche, die ausschließlich oder vorwiegend dem Flugverkehr dient.','{"ax_tatsaechlichenutzung"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Nutzung_Flugverkehr','NTZ','''Nutzung'' gibt den Nutzerkreis von ''Flugverkehr'' an.','ax_flugverkehr','DLKM','nutzung');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('3000','Teils zivil, teils militärisch','''Teils zivil, teils militärisch'' bedeutet dass ''''Flugverkehr'' sowohl zivil als auch militärisch genutzt wird.','nutzung','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Militärisch','''Militärisch'' bedeutet, dass ''Flugverkehr'' nur von Streitkräften genutzt wird.','nutzung','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Zivil','''Zivil'' bedeutet, dass ''Flugverkehr'' privaten, öffentlichen oder religiösen Zwecken dient und nicht militärisch genutzt wird.','nutzung','ax_flugverkehr');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Funktion_Flugverkehr','FKT','''Funktion'' ist die zum Zeitpunkt der Erhebung vorherrschende Nutzung (Dominanzprinzip).','ax_flugverkehr','DLKM','funktion');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('5501','Gebäude- und Freifläche zu Verkehrsanlagen, Luftfahrt','funktion','ax_flugverkehr');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Art_Flugverkehr','ART','''Art'' ist Einstufung der Flugverkehrsfläche durch das Luftfahrtbundesamt.','ax_flugverkehr','DLKM','art');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5520','Verkehrslandeplatz','''Verkehrslandeplatz'' ist ein Flugplatz, der in der Luftfahrtkarte 1:500000 (ICAO) als solcher ausgewiesen ist.','art','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5511','Internationaler Flughafen','''Internationaler Flughafen'' ist ein Flughafen, der in der Luftfahrtkarte 1 : 500000 (ICAO) als solcher ausgewiesen ist.','art','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5540','Landeplatz, Sonderlandeplatz','''Landeplatz, Sonderlandeplatz'' ist eine Fläche, die in der Luftfahrtkarte 1:500000 (ICAO) als Landeplatz, Sonderlandeplatz ausgewiesen ist.','art','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5530','Hubschrauberflugplatz','''Hubschrauberflugplatz'' ist ein Flugplatz, der in der Luftfahrtkarte 1:500000 (ICAO) als solcher ausgewiesen ist.','art','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5510','Flughafen','''Flughafen'' ist eine Anlage mit Gebäuden, Bauwerken, Start- und Landebahnen sowie sonstigen flugtechnischen Einrichtungen zur Abwicklung des Flugverkehrs.','art','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5512','Regionalflughafen','''Regionalflughafen'' ist ein Flughafen der gemäß Raumordnungsgesetz als Regionalflughafen eingestuft ist.','art','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5550','Segelfluggelände','''Segelfluggelände'' ist eine Fläche, die in der Luftfahrtkarte 1:500000 (ICAO) als Segelfluggelände ausgewiesen ist.','art','ax_flugverkehr');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Lagebezeichnung','NAM','''Name'' ist der Eigenname von ''Flugverkehr''.','ax_flugverkehr','DLKM','name');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','CharacterString','BEZ','''Bezeichnung'' ist die von einer Fachstelle vergebene Kennziffer von ''Flugverkehr''.','ax_flugverkehr','DLKM','bezeichnung');
