INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4000','Im Bau','''Im Bau'' bedeutet, dass ''Flugverkehr'' noch nicht fertiggestellt ist.','zustand','ax_flugverkehr');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Außer Betrieb, stillgelegt, verlassen','''Außer Betrieb, stillgelegt; verlassen'' bedeutet, dass sich ''Flugverkehr'' nicht mehr in regelmäßiger, der Bestimmung entsprechenden Nutzung befindet.','zustand','ax_flugverkehr');
INSERT INTO alkis_elemente(konsistenzbedingungen,name,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('Die Attributart ''Produkt'' kann nur in Verbindung mit der Attributart ''Bauwerksfunktion'' und den Wertearten 1101 und 1103 vorkommen.','ax_transportanlage','51004','REO','[E] ''Transportanlage'' ist eine Anlage zur Förderung oder zum Transport von Flüssigkeiten, Gasen und Gütern.','{"ax_bauwerkeeinrichtungenundsonstigeangaben","au_objekt"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Produkt_Transportanlage','PRO','''Produkt'' gibt an, welches Produkt transportiert wird.','ax_transportanlage','DLKM','produkt');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1131','Trinkwasser','''Trinkwasser'' ist ein für den menschlichen Genuss und Gebrauch geeignetes Wasser, das transportiert wird.','produkt','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1120','Gas','''Gas'' ist eine gasförmige und brennbare Materie, die gefördert oder transportiert wird.','produkt','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1130','Wasser','''Wasser'' ist die chemische Verbindung von Wasserstoff mit Sauerstoff, die gefördert oder transportiert wird.','produkt','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1132','Brauchwasser','''Brauchwasser'' ist ein für technische u.a. Zwecke verwendetes Wasser, das transportiert wird.','produkt','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1140','Fernwärme','produkt','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1110','Erdöl','''Erdöl'' ist ein flüssiges und brennbares Kohlenwasserstoffgemisch, das gefördert oder transportiert wird.','produkt','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1133','Abwasser','''Abwasser'' ist ein verunreinigtes Wasser aus Haushaltungen, Gewerbe- und Industriebetrieben sowie Niederschlagswasser, das transportiert wird.','produkt','ax_transportanlage');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_LageZurErdoberflaeche_Transportanlage','OFL','''Lage zur Erdoberfläche'' ist die Angabe der relativen Lage von ''Transportanlage'' zur Erdoberfläche.','ax_transportanlage','DLKM','lagezurerdoberflaeche');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1400','Aufgeständert','''Aufgeständert'' bedeutet, dass eine Transportanlage durch Tragwerke (Stützen, Pfeiler) über das Niveau der Erdoberfläche geführt wird, um z.B. Hindernisse zu überwinden.','lagezurerdoberflaeche','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Unter der Erdoberfläche','''Unter der Erdoberfläche'' bedeutet, dass sich die Transportanlage unter der Erdoberfläche befindet.','lagezurerdoberflaeche','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1700','Unter der Wasseroberfläche','''Unter der Wasseroberfläche'' bedeutet, dass sich eine Transportanlage unter der Wasseroberfläche befindet.','lagezurerdoberflaeche','ax_transportanlage');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('1','AX_Bauwerksfunktion_Transportanlage','BWF','''Bauwerksfunktion'' beschreibt die Art oder Funktion von ''Transportanlage''.','ax_transportanlage','DLKM','bauwerksfunktion');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1102','Förderband, Bandstraße','''Förderband, Bandstraße'' ist ein mechanisch bewegtes Band zum Transport von Gütern.','bauwerksfunktion','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1101','Rohrleitung, Pipeline','''Rohrleitung, Pipeline'' ist ein langgestreckter Hohlkörper zum Transport von Flüssigkeiten und Gasen.','bauwerksfunktion','ax_transportanlage');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1103','Pumpe','''Pumpe'' ist eine Vorrichtung zum An-, Absaugen oder Injizieren von Flüssigkeiten oder Gasen; Verdichtungsstation für Gase.','bauwerksfunktion','ax_transportanlage');
INSERT INTO alkis_elemente(konsistenzbedingungen,name,kennung,objekttyp,definition,abgeleitet_aus,modellart,type,erfassungskriterien) VALUES ('Beim Attribut ''Bahnkategorie'' muss für die Modellart DLKM die Kardinalität 1..* eingehalten werden.','ax_gleis','53006','REO','[E] ''Gleis'' ist ein zur Führung von Schienenfahrzeugen verlegtes Schienenpaar.','{"ax_bauwerkeeinrichtungenundsonstigeangaben","au_objekt"}','DLKM','Objektart','Modelliert ist die Mittellinie der Gleisachse. Die Drehscheibe ist als Fläche modelliert.');
