INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Abbaugut_TagebauGrubeSteinbruch','AGT','''Abbaugut'' gibt an, welches Material abgebaut wird.','ax_tagebaugrubesteinbruch','DLKM','abbaugut');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5005','Kalkspat','''Kalkspat'' ist ein weißes oder hell gefärbtes Abbaugut (Calciumcarbonat).','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5010','Pegmatitsand','''Pegmatitsand'' ist ein Abbaugut, das durch Verwitterung von Granit und Gneis entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2021','Talkschiefer, Speckstein','''Talkschiefer, Speckstein'' ist ein farbloses bis graugrünes, sich fettig anfühlendes Abbaugut, das aus dem weichen Mineral Talk besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4010','Torf','''Torf'' ist ein Abbaugut, das aus der unvollkommenen Zersetzung abgestorbener pflanzlicher Substanz unter Luftabschluss in Mooren entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2003','Metamorpher Schiefer','''Metamorpher Schiefer'' ist ein Abbaugut, dessen ursprüngliche Zusammensetzung und Struktur durch Wärme und Druck innerhalb der Erdkruste verändert worden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2010','Grauwacke','''Grauwacke'' ist ein Abbaugut, das aus tonhaltigem Sandstein besteht und mit Gesteinsbruchstücken angereichert sein kann.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1009','Kies, Kiessand','''Kies, Kiessand'' ist ein Abbaugut, das aus vom Wasser rund geschliffenen Gesteinsbrocken besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5000','Industrieminerale, Salze','''Industrieminerale, Salze'' bedeutet, dass die in der Natur vorkommenden Mineralien abgebaut werden.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2016','Granit','''Granit'' ist ein eruptiv entstandenes Abbaugut, das aus körnigem Feldspat, Quarz, Glimmer besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5008','Quarz','''Quarz'' ist ein Abbaugut, das aus verschiedenen Gesteinsarten (Granit, Gneis, Sandstein) gewonnen wird.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2008','Marmor','''Marmor'' ist ein Abbaugut, das als rein weißer kristalliner, körniger Kalkstein (Calciumcarbonat) vorkommt.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1001','Ton','''Ton'' ist ein Abbaugut, das aus gelblichem bis grauem Lockergestein besteht und durch Verwitterung älterer Gesteine entsteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2019','Trass','''Trass'' ist ein Abbaugut, das aus vulkanischem Aschentuff (Bimsstein) besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1000','Erden, Lockergestein','''Erden, Lockergestein'' bedeutet, dass feinkörnige Gesteine abgebaut werden.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4020','Kohle','''Kohle'' ist ein Abbaugut, das durch Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1008','Sand','''Sand'' ist ein Abbaugut, das aus kleinen, losen Mineralkörnern (häufig Quarz) besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2001','Tonstein','''Tonstein'' ist ein gelblich bis graues Abbaugut, das überwiegend aus Tonmineralien besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5002','Anhydritstein','''Anhydritstein'' ist ein Abbaugut, das aus wasserfreiem Gips besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1004','Lehm','''Lehm'' ist ein Abbaugut, das durch Verwitterung entstanden ist und aus gelb bis braun gefärbtem sandhaltigem Ton besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1012','Quarzsand','''Quarzsand'' ist ein Abbaugut, das vorwiegend aus kleinen, losen Quarzkörnern besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5009','Feldspat','''Feldspat'' ist ein weiß bis grauweißes gesteinsbildendes Mineral von blättrigem Bruch, das abgebaut wird.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4030','Ölschiefer','''Ölschiefer'' ist ein Abbaugut, das aus dunklem, bitumenhaltigen, tonigen Gestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1011','Farberden','''Farberden'' ist ein Abbaugut, das durch Verwitterung entstanden ist und vorrangig aus eisenhaltigem Gestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1002','Bentonit','''Bentonit'' ist ein tonartiges Abbaugut, das durch Verwitterung vulkanischer Asche (Tuffe) entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1005','Löß, Lößlehm','''Löß, Lößlehm'' ist ein Abbaugut das aus feinsten gelblichen Sedimenten besteht und eine hohe Wasserspeicherfähigkeit aufweist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2007','Travertin','''Travertin'' ist ein Abbaugut, das aus gelblichen Kiesel- oder Kalktuffen besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2004','Mergelstein','''Mergelstein'' ist ein Abbaugut, das sich größtenteils aus Ton und Kalk zusammensetzt.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1007','Kalk, Kalktuff, Kreide','''Kalk, Kalktuff, Kreide'' ist ein Abbaugut, das aus erdigem weißen Kalkstein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2014','Andesit','''Andesit'' ist ein Abbaugut, das aus Ergussgestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4021','Braunkohle','''Braunkohle'' ist ein Abbaugut, das durch einen bestimmten Grad von Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2011','Quarzit','''Quarzit'' ist ein sehr hartes metamorphes Abbaugut, das vorwiegend aus feinkörnigen Quarzmineralien besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2018','Tuff-, Bimsstein','''Tuff-, Bimsstein'' ist ein helles, sehr poröses Abbaugut, das durch rasches Erstarren der Lava entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1013','Kieselerde','''Kieselerde'' ist ein Abbaugut, das durch tertiäre Binnenseeablagerungen aus Kieselschalen toter Kieselalgen entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2013','Basalt, Diabas','''Basalt, Diabas'' ist ein Abbaugut, das aus basischem Ergussgestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1006','Mergel','''Mergel'' ist ein Abbaugut das aus kalk- und tonartigem Sedimentgestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1003','Kaolin','''Kaolin'' ist ein Abbaugut, das aus weißem, erdigem Gestein, fast reinem Aluminiumsilikat (kieselsaure Tonerde) besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('9999','Sonstiges','''Sonstiges'' bedeutet, dass das Abbaugut bekannt, aber nicht in der Attributwertliste aufgeführt ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2009','Sandstein','''Sandstein'' ist ein Abbaugut, das aus verfestigtem Sedimentgestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2002','Schiefer, Dachschiefer','''Schiefer, Dachschiefer'' ist ein toniges Abbaugut, das in dünne ebene Platten spaltbar ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2017','Granodiorit','Granodiorit'' ist ein hell- bis dunkelgraues Abbaugut. Es ist ein mittelkörniges Tiefengestein mit den Hauptbestandteilen Feldspat, Quarz, Hornblende und Biotit.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4000','Treib- und Brennstoffe','''Treib- und Brennstoffe'' bedeutet, dass die in der Natur vorkommenden brennbaren organischen und anorganischen Substanzen abgebaut oder gewonnen werden.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2015','Porphyr, Quarzporphyr','''Porphyr, Quarzporphyr'' ist ein eruptiv entstandenes Abbaugut, das aus einer dichten Grundmasse und groben Einsprenglingen besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5007','Schwerspat','''Schwerspat'' ist ein formenreiches, rhombisches weißes bis farbiges Abbaugut.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2005','Kalkstein','''Kalkstein'' ist ein Abbaugut, das als weit verbreitetes Sedimentgestein überwiegend aus Calciumcarbonat besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2006','Dolomitstein','''Dolomitstein'' ist ein Abbaugut, das überwiegend aus calcium- und magnesiumhaltigen Mineralien besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2020','Lavaschlacke','''Lavaschlacke'' ist ein Abbaugut, das aus ausgestoßenem, geschmolzenen Vulkangestein besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('4022','Steinkohle','''Steinkohle'' ist ein Abbaugut, das durch vollständige Inkohlung (Umwandlungsprozess pflanzlicher Substanzen) entstanden ist.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('5001','Gipsstein','''Gipsstein'' ist ein natürliches Abbaugut.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2000','Steine, Gestein, Festgestein','''Steine, Gestein, Festgestein'' bedeutet, dass grobkörnige oder feste Gesteine abgebaut werden.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2012','Gneis','''Gneis'' ist ein metamorphes Abbaugut mit Schieferung, das aus Feldspat, Quarz und Glimmer besteht.','abbaugut','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Zustand_TagebauGrubeSteinbruch','ZUS','''Zustand'' beschreibt die Betriebsbereitschaft von ''Tagebau, Grube, Steinbruch''.','ax_tagebaugrubesteinbruch','DLKM','zustand');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('8000','Erweiterung, Neuansiedlung','zustand','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('2100','Außer Betrieb, stillgelegt, verlassen','''Außer Betrieb, stillgelegt; verlassen'' bedeutet, dass sich ''Tagebau, Grube, Steinbruch'' nicht mehr in regelmäßiger, der Bestimmung entsprechenden Nutzung befindet.','zustand','ax_tagebaugrubesteinbruch');
INSERT INTO alkis_elemente(name,grunddatenbestand,kennung,objekttyp,definition,abgeleitet_aus,modellart,type) VALUES ('ax_landwirtschaft','DLKM','43001','REO','[E] ''Landwirtschaft'' ist eine Fläche für den Anbau von Feldfrüchten sowie eine Fläche, die beweidet und gemäht werden kann, einschließlich der mit besonderen Pflanzen angebauten Fläche. Die Brache, die für einen bestimmten Zeitraum (z. B. ein halbes oder ganzes Jahr) landwirtschaftlich unbebaut bleibt, ist als ''Landwirtschaft'' bzw. ''Ackerland'' zu erfassen.','{"ax_tatsaechlichenutzung"}','DLKM','Objektart');
INSERT INTO alkis_attributart(kardinalitaet,datentyp,kennung,definition,element,modellart,bezeichnung) VALUES ('0..1','AX_Vegetationsmerkmal_Landwirtschaft','VEG','''Vegetationsmerkmal'' ist die zum Zeitpunkt der Erhebung erkennbare oder feststellbare vorherrschend vorkommende landwirtschaftliche Nutzung (Dominanzprinzip).','ax_landwirtschaft','DLKM','vegetationsmerkmal');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1011','Streuobstacker','''Streuobstacker'' beschreibt den Bewuchs einer Ackerfläche mit Obstbäumen.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1012','Hopfen','''Hopfen'' ist eine mit speziellen Vorrichtungen ausgestattete Agrarfläche für den Anbau von Hopfen.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1010','Ackerland','''Ackerland'' ist eine Fläche für den Anbau von Feldfrüchten (z.B. Getreide, Hülsenfrüchte, Hackfrüchte) und Beerenfrüchten (z.B. Erdbeeren). Zum Ackerland gehören auch die Rotationsbrachen, Dauerbrachen sowie Flächen, die zur Erlangung der Ausgleichszahlungen der EU stillgelegt worden sind.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1030','Gartenland','''Gartenland'' ist eine Fläche für den Anbau von Gemüse, Obst und Blumen sowie für die Aufzucht von Kulturpflanzen.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1050','Obstplantage','''Obstplantage'' ist eine landwirtschaftliche Fläche, die mit Obstbäumen und Obststräuchern bepflanzt ist','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1200','Brachland','''Brachland'' ist eine Fläche der Landwirtschaft, die seit längerem nicht mehr zu Produktionszwecken genutzt wird.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1013','Spargel','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1021','Streuobstwiese','''Streuobstwiese'' beschreibt den Bewuchs einer Grünlandfläche mit Obstbäumen.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1051','Obstbaumplantage','''Obstbaumplantage'' ist eine landwirtschaftliche Fläche, die ausschließlich mit Obstbäumen bepflanzt ist.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1040','Weingarten','''Weingarten'' ist eine mit speziellen Vorrichtungen ausgestattete Agrarfläche auf der Weinstöcke angepflanzt sind.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1031','Baumschule','''Baumschule'' ist eine Fläche, auf der Holzgewächse aus Samen, Ablegern oder Stecklingen unter mehrmaligem Umpflanzen (Verschulen) gezogen werden.','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,bezeichnung,element) VALUES ('1052','Obststrauchplantage','vegetationsmerkmal','ax_landwirtschaft');
INSERT INTO alkis_wertearten(k,v,d,bezeichnung,element) VALUES ('1020','Grünland','''Grünland'' ist eine Grasfläche, die gemäht oder beweidet wird.','vegetationsmerkmal','ax_landwirtschaft');
